// GetMetadataFromDDSMemory/File
bool Test01()
{
    bool success = true;

    // GetMetadataFromDDSMemory is used internally to the File version, so we only need to explicitly test the File version

    size_t ncount = 0;
    size_t npass = 0;

    for( size_t index=0; index < std::size(g_TestMedia); ++index )
    {
        wchar_t szPath[MAX_PATH] = {};
        DWORD ret = ExpandEnvironmentStringsW(g_TestMedia[index].fname, szPath, MAX_PATH);
        if ( !ret || ret > MAX_PATH )
        {
            printe( "ERROR: ExpandEnvironmentStrings FAILED\n" );
            return false;
        }

#ifdef _DEBUG
        OutputDebugString(szPath);
        OutputDebugStringA("\n");
#endif

        const auto flags = GetTestDDSFlags(g_TestMedia[index].options);
        bool pass = false;

        // FromMemory
        Blob blob;
        HRESULT hr = LoadBlobFromFile(szPath, blob);
        if (FAILED(hr))
        {
            success = false;
            printe("Failed getting raw file data from (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath);
        }
        else
        {
            TexMetadata metadata;
            hr = GetMetadataFromDDSMemory(blob.GetConstBufferPointer(), blob.GetBufferSize(), flags, metadata);

            const TexMetadata* check = &g_TestMedia[index].metadata;
            if (FAILED(hr))
            {
                success = false;
                printe("Failed getting data from memory (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath);
            }
            else if (!IsCorrectMetadata(metadata, *check, g_TestMedia[index].options))
            {
                success = false;
                printe("Metadata error in:\n%ls\n", szPath);
                printmeta(&metadata);
                printmetachk(check);
            }
            else
            {
                pass = true;
            }
        }

        // FromFile
        TexMetadata metadata;
        hr = GetMetadataFromDDSFile(szPath, flags, metadata);

        const TexMetadata* check = &g_TestMedia[index].metadata;
        if ( FAILED(hr) )
        {
            success = false;
            printe( "Failed getting data from (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
        }
        else if (!IsCorrectMetadata(metadata, *check, g_TestMedia[index].options))
        {
            success = false;
            printe("Metadata error in:\n%ls\n", szPath);
            printmeta(&metadata);
            printmetachk(check);
        }
        else
        {
            pass = true;

            if (g_TestMedia[index].options & FLAGS_EXPAND)
            {
                TexMetadata metadata2;
                hr = GetMetadataFromDDSFile(szPath, flags | DDS_FLAGS_NO_LEGACY_EXPANSION, metadata2);

                if (SUCCEEDED(hr))
                {
                    success = pass = false;
                    printe("no legacy expansion flag ignored in:\n%ls\n", szPath);
                }
            }

            if (g_TestMedia[index].options & FLAGS_BGR)
            {
                TexMetadata metadata2;
                hr = GetMetadataFromDDSFile(szPath, flags | DDS_FLAGS_FORCE_RGB, metadata2);

                if (FAILED(hr) || (metadata2.format != DXGI_FORMAT_R8G8B8A8_UNORM
                    && metadata2.format != DXGI_FORMAT_R8G8B8A8_UNORM_SRGB
                    && metadata2.format != DXGI_FORMAT_R8G8B8A8_TYPELESS))
                {
                    success = pass = false;
                    printe("Failed to force RGB:\n%ls\n", szPath);
                }
            }

            if (g_TestMedia[index].options & FLAGS_LUMINANCE)
            {
                TexMetadata metadata2;
                hr = GetMetadataFromDDSFile(szPath, flags | DDS_FLAGS_EXPAND_LUMINANCE, metadata2);

                if (FAILED(hr) || (metadata2.format != DXGI_FORMAT_R8G8B8A8_UNORM
                    && metadata2.format != DXGI_FORMAT_R16G16B16A16_UNORM))
                {
                    success = pass = false;
                    printe("Failed to expand LUMINANCE expansion:\n%ls\n", szPath);
                }
            }

            switch (static_cast<int>(metadata.format))
            {
            case DXGI_FORMAT_B5G6R5_UNORM:
            case DXGI_FORMAT_B5G5R5A1_UNORM:
            case DXGI_FORMAT_B4G4R4A4_UNORM:
            case WIN11_DXGI_FORMAT_A4B4G4R4_UNORM:
                {
                    TexMetadata metadata2;
                    hr = GetMetadataFromDDSFile(szPath, flags | DDS_FLAGS_NO_16BPP, metadata2);

                    if (FAILED(hr) || (metadata2.format == metadata.format) || (BitsPerPixel(metadata2.format) == 16))
                    {
                        success = pass = false;
                        printe("Failed to force no 16bpp formats:\n%ls\n", szPath);
                    }
                }
                break;
            }
        }

        if (pass)
            ++npass;

        ++ncount;
    }

    print("%zu images tested, %zu images passed ", ncount, npass);

    // invalid args
    {
    #pragma warning(push)
    #pragma warning(disable:6385 6387)
        TexMetadata metadata;
        HRESULT hr = GetMetadataFromDDSMemory(static_cast<const uint8_t*>(nullptr), 0, DDS_FLAGS_NONE, metadata);
        if (hr != E_INVALIDARG)
        {
            success = false;
            printe("Failed invalid arg mem test\n");
        }

        hr = GetMetadataFromDDSFile(nullptr, DDS_FLAGS_NONE, metadata);
        if (hr != E_INVALIDARG)
        {
            success = false;
            printe("Failed invalid arg file test\n");
        }

        hr = GetMetadataFromDDSFile(L"TestFileDoesNotExist.DDS", DDS_FLAGS_NONE, metadata);
        if (hr != HRESULT_FROM_WIN32(ERROR_FILE_NOT_FOUND))
        {
            success = false;
            printe("Failed missing file test (HRESULT: %08X)\n", static_cast<unsigned int>(hr));
        }
    #pragma warning(pop)
    }

    return success;
}

