// SaveDDSToMemory
bool Test04()
{
    bool success = true;

    size_t ncount = 0;
    size_t npass = 0;

    for( size_t index=0; index < std::size(g_SaveMedia); ++index )
    {
        wchar_t szPath[MAX_PATH] = {};
        DWORD ret = ExpandEnvironmentStringsW(g_SaveMedia[index].fname, szPath, MAX_PATH);
        if ( !ret || ret > MAX_PATH )
        {
            printe( "ERROR: ExpandEnvironmentStrings FAILED\n" );
            return false;
        }

#ifdef _DEBUG
        OutputDebugString(szPath);
        OutputDebugStringA("\n");
#endif

        TexMetadata metadata;
        ScratchImage image;
        HRESULT hr = LoadFromDDSFile( szPath, g_SaveMedia[index].ddsflags, &metadata, image );

        if ( FAILED(hr) )
        {
            success = false;
            printe( "Failed loading DDS from (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
        }
        else
        {
            bool pass = true;

            uint8_t digest[16];
            hr = MD5Checksum( image, digest );
            if ( FAILED(hr) )
            {
                success = false;
                pass = false;
                printe( "Failed computing MD5 checksum of image data (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
            }
            else
            {
                Blob blob;
                hr = SaveToDDSMemory( image.GetImages(), image.GetImageCount(), image.GetMetadata(), DDS_FLAGS_NONE, blob );
                if ( FAILED(hr) )
                {
                    success = false;
                    pass = false;
                    printe( "Failed writing DDS to memory (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                }
                else
                {
                    TexMetadata metadata2;
                    ScratchImage image2;
                    hr = LoadFromDDSMemory( blob.GetConstBufferPointer(), blob.GetBufferSize(), DDS_FLAGS_NONE, &metadata2, image2 );
                    if ( FAILED(hr) )
                    {
                        success = false;
                        pass = false;
                        printe( "Failed reading back written DDS to memory (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                    }
                    else
                    {
                        TexMetadata chk = metadata;

                        // For legacy headers, we lose miscFlags2 information except for the case of PM alpha for BC2/BC3
                        if ( chk.miscFlags2 != 0 )
                        {
                            if ( chk.IsPMAlpha() && ((chk.format == DXGI_FORMAT_BC2_UNORM) || (chk.format == DXGI_FORMAT_BC3_UNORM)) )
                            {
                                chk.miscFlags2 = TEX_ALPHA_MODE_PREMULTIPLIED;
                            }
                            else
                            {
                                chk.miscFlags2 = 0;
                            }
                        }

                        if ( ( chk.dimension == TEX_DIMENSION_TEXTURE1D
                               && metadata2.dimension != TEX_DIMENSION_TEXTURE1D
                               && metadata2.dimension != TEX_DIMENSION_TEXTURE2D )
                             || ( chk.dimension != TEX_DIMENSION_TEXTURE1D
                                  && chk.dimension != metadata2.dimension ) )
                        {
                            success = false;
                            pass = false;
                            printe( "Metadata error in dds dim memory readback:\n%ls\n", szPath );
                            printmeta( &metadata2 );
                            printmetachk( &chk );
                        }
                        else if ( chk.width != metadata2.width
                                  || chk.height != metadata2.height
                                  || chk.arraySize != metadata2.arraySize
                                  || chk.mipLevels != metadata2.mipLevels
                                  || chk.miscFlags != metadata2.miscFlags
                                  || chk.miscFlags2 != metadata2.miscFlags2 )
                        {
                            success = false;
                            pass = false;
                            printe( "Metadata error in dds memory readback:\n%ls\n", szPath );
                            printmeta( &metadata2 );
                            printmetachk( &chk );
                        }
                        else
                        {
                            uint8_t digest2[16];
                            hr = MD5Checksum( image2, digest2 );
                            if ( FAILED(hr) )
                            {
                                success = false;
                                pass = false;
                                printe( "Failed computing MD5 checksum of reloaded image data (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                            }
                            else if ( memcmp( digest, digest2, 16 ) != 0 )
                            {
                                success = false;
                                pass = false;
                                printe( "MD5 checksum of reloaded data doesn't match original:\n%ls\n", szPath );
                            }
                        }
                    }
                }

                // Test flag to force DX10 extended header use
                Blob blob2;
                hr = SaveToDDSMemory( image.GetImages(), image.GetImageCount(), image.GetMetadata(), DDS_FLAGS_FORCE_DX10_EXT, blob2 );
                if ( FAILED(hr) )
                {
                    success = false;
                    pass = false;
                    printe( "Failed writing DDS with DDS_FLAGS_FORCE_DX10_EXT to memory (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                }
                else
                {
                    TexMetadata metadata2;
                    ScratchImage image2;
                    hr = LoadFromDDSMemory( blob2.GetConstBufferPointer(), blob2.GetBufferSize(), DDS_FLAGS_NONE, &metadata2, image2 );
                    if ( FAILED(hr) )
                    {
                        success = false;
                        pass = false;
                        printe( "Failed reading back written DDS with DDS_FLAGS_FORCE_DX10_EXT to memory (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                    }
                    else if ( metadata.width != metadata2.width
                              || metadata.height != metadata2.height
                              || metadata.arraySize != metadata2.arraySize
                              || metadata.mipLevels != metadata2.mipLevels
                              || metadata.dimension != metadata2.dimension
                              || metadata.miscFlags != metadata2.miscFlags
                              || metadata2.miscFlags2 != 0 ) // For "DX10" headers compatible with D3DX10/D3DX11, we always lose miscFlags2
                    {
                        success = false;
                        pass = false;
                        printe( "Metadata error in dds with DDS_FLAGS_FORCE_DX10_EXT readback from memory:\n%ls\n", szPath );
                        printmeta( &metadata2 );
                        printmetachk( &metadata );
                    }
                    else
                    {
                        hr = IsDDSHeaderPresent( blob2.GetConstBufferPointer(), blob2.GetBufferSize() );
                        if ( FAILED(hr) )
                        {
                            success = false;
                            pass = false;
                            printe( "Failed checking DDS header type in memory (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                        }
                        else if ( hr == S_FALSE )
                        {
                            success = false;
                            pass = false;
                            printe( "Write with DDS_FLAGS_FORCE_DX10_EXT did not result in DX10 extended header in memory:\n%ls\n", szPath );
                        }
                        else
                        {
                            uint8_t digest2[16];
                            hr = MD5Checksum( image2, digest2 );
                            if ( FAILED(hr) )
                            {
                                success = false;
                                pass = false;
                                printe( "Failed computing MD5 checksum of DDS_FLAGS_FORCE_DX10_EXT reloaded image data (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                            }
                            else if ( memcmp( digest, digest2, 16 ) != 0 )
                            {
                                success = false;
                                pass = false;
                                printe( "MD5 checksum of DDS_FLAGS_FORCE_DX10_EXT reloaded data doesn't match original:\n%ls\n", szPath );
                            }
                        }
                    }
                }

                // Test flag to force DX10 extended header with miscflags2 data use
                blob2.Release();
                hr = SaveToDDSMemory( image.GetImages(), image.GetImageCount(), image.GetMetadata(), DDS_FLAGS_FORCE_DX10_EXT_MISC2, blob2 );
                if ( FAILED(hr) )
                {
                    success = false;
                    pass = false;
                    printe( "Failed writing DDS with DDS_FLAGS_FORCE_DX10_EXT_MISC2 to memory (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                }
                else
                {
                    TexMetadata metadata2;
                    ScratchImage image2;
                    hr = LoadFromDDSMemory( blob2.GetConstBufferPointer(), blob2.GetBufferSize(), DDS_FLAGS_NONE, &metadata2, image2 );
                    if ( FAILED(hr) )
                    {
                        success = false;
                        pass = false;
                        printe( "Failed reading back written DDS with DDS_FLAGS_FORCE_DX10_EXT_MISC2 to memory (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                    }
                    else if ( metadata.width != metadata2.width
                              || metadata.height != metadata2.height
                              || metadata.arraySize != metadata2.arraySize
                              || metadata.mipLevels != metadata2.mipLevels
                              || metadata.dimension != metadata2.dimension
                              || metadata.miscFlags != metadata2.miscFlags
                              || metadata.miscFlags2 != metadata2.miscFlags2 )
                    {
                        success = false;
                        pass = false;
                        printe( "Metadata error in dds with DDS_FLAGS_FORCE_DX10_EXT_MISC2 readback from memory:\n%ls\n", szPath );
                        printmeta( &metadata2 );
                        printmetachk( &metadata );
                    }
                    else
                    {
                        hr = IsDDSHeaderPresent( blob2.GetConstBufferPointer(), blob2.GetBufferSize() );
                        if ( FAILED(hr) )
                        {
                            success = false;
                            pass = false;
                            printe( "Failed checking DDS header type in memory (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                        }
                        else if ( hr == S_FALSE )
                        {
                            success = false;
                            pass = false;
                            printe( "Write with DDS_FLAGS_FORCE_DX10_EXT_MISC2 did not result in DX10 extended header in memory:\n%ls\n", szPath );
                        }
                        else
                        {
                            uint8_t digest2[16];
                            hr = MD5Checksum( image2, digest2 );
                            if ( FAILED(hr) )
                            {
                                success = false;
                                pass = false;
                                printe( "Failed computing MD5 checksum of DDS_FLAGS_FORCE_DX10_EXT_MISC2 reloaded image data (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                            }
                            else if ( memcmp( digest, digest2, 16 ) != 0 )
                            {
                                success = false;
                                pass = false;
                                printe( "MD5 checksum of DDS_FLAGS_FORCE_DX10_EXT_MISC2 reloaded data doesn't match original:\n%ls\n", szPath );
                            }
                        }
                    }
                }

                // Test larger case of a row pitch that is larger than 1-byte alignment
                if ( !IsCompressed( metadata.format ) && !IsPacked( metadata.format ) && !IsPlanar( metadata.format ) )
                {
                    blob2.Release();
                    ScratchImage imageWide;
                    hr = CreateWideImage( image.GetImages(), image.GetImageCount(), image.GetMetadata(), imageWide );
                    if ( FAILED(hr) )
                    {
                        success = false;
                        pass = false;
                        printe( "Failed creating wide test image (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                    }
                    else if ( image.GetImage(0,0,0)->rowPitch >= imageWide.GetImage(0,0,0)->rowPitch )
                    {
                        success = false;
                        pass = false;
                        printe( "Failed creating wide test image - result is same as source: %zu %zu\n%ls\n", image.GetImage(0,0,0)->rowPitch, imageWide.GetImage(0,0,0)->rowPitch, szPath );
                    }
                    else
                    {
                        const DDS_FLAGS ddsflags = ( metadata.dimension == TEX_DIMENSION_TEXTURE1D ) ? DDS_FLAGS_FORCE_DX10_EXT : DDS_FLAGS_NONE;

                        hr = SaveToDDSMemory( imageWide.GetImages(), imageWide.GetImageCount(), imageWide.GetMetadata(), ddsflags, blob2 );
                        if ( FAILED(hr) )
                        {
                            success = false;
                            pass = false;
                            printe( "Failed writing DDS with wide image to memory (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                        }
                        else
                        {
                            TexMetadata metadata2;
                            ScratchImage image2;
                            hr = LoadFromDDSMemory( blob2.GetConstBufferPointer(), blob2.GetBufferSize(), DDS_FLAGS_NONE, &metadata2, image2 );
                            if ( FAILED(hr) )
                            {
                                success = false;
                                pass = false;
                                printe( "Failed reading back written DDS with wide image to memory (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                            }
                            else if ( metadata.width != metadata2.width
                                      || metadata.height != metadata2.height
                                      || metadata.arraySize != metadata2.arraySize
                                      || metadata.mipLevels != metadata2.mipLevels
                                      || metadata.dimension != metadata2.dimension
                                      || metadata.miscFlags != metadata2.miscFlags
                                      /* Ignore metadata.miscFlags2 */ )
                            {
                                success = false;
                                pass = false;
                                printe( "Metadata error in dds with wide image readback from memory:\n%ls\n", szPath );
                                printmeta( &metadata2 );
                                printmetachk( &metadata );
                            }
                            else
                            {
                                uint8_t digest2[16];
                                hr = MD5Checksum( image2, digest2 );
                                if ( FAILED(hr) )
                                {
                                    success = false;
                                    pass = false;
                                    printe( "Failed computing MD5 checksum of wide image reloaded image data (HRESULT %08X):\n%ls\n", static_cast<unsigned int>(hr), szPath );
                                }
                                else if ( memcmp( digest, digest2, 16 ) != 0 )
                                {
                                    success = false;
                                    pass = false;
                                    printe( "MD5 checksum of wide image reloaded data doesn't match original:\n%ls\n", szPath );
                                }
                            }
                        }
                    }
                }
            }

            if ( pass )
            {
                ++npass;
            }
        }

        ++ncount;
    }

    print("%zu images tested, %zu images passed ", ncount, npass );

    // invalid args
    {
    #pragma warning(push)
    #pragma warning(disable:6385 6387)
        Image nullin = {};
        nullin.width = nullin.height = 256;
        nullin.format = DXGI_FORMAT_R8G8B8A8_UNORM;
        Blob result;
        HRESULT hr = SaveToDDSMemory(nullin, DDS_FLAGS_NONE, result);
        if (hr != E_INVALIDARG && hr != E_POINTER)
        {
            success = false;
            printe("Failed invalid arg test\n");
        }

        TexMetadata metadata = {};
        metadata.width = metadata.height = 256;
        metadata.format = DXGI_FORMAT_R8G8B8A8_UNORM;
        metadata.depth = metadata.arraySize = metadata.mipLevels = 1;
        metadata.dimension = TEX_DIMENSION_TEXTURE2D;
        hr = SaveToDDSMemory(nullptr, 0, metadata, DDS_FLAGS_NONE, result);
        if (hr != E_INVALIDARG)
        {
            success = false;
            printe("Failed invalid arg complex test\n");
        }

        metadata.format = DXGI_FORMAT_R8G8B8A8_UNORM;
    #pragma warning(pop)
    }

    return success;
}


